#include <iostream>
#include <vector>
#include "array.h"

using namespace std;

class Solution
{
public:
    vector<int> findAnagrams(string s, string p)
    {
        if (s.size() < p.size())
        {
            return {};
        }

        int ptable[26] = {0};
        int ptableBak[26] = {0};
        for (int i = 0; i < p.size(); i++)
        {
            ptable[p[i] - 'a']--;
            ptableBak[p[i] - 'a']--;
        }

        vector<int> result;
        for (int i = 0; i <= s.size() - p.size(); i++)
        {
            for (int k = 0; k < 26; k++)
            {
                ptable[k] = ptableBak[k];
            }
            int *table = anagramDiff(s, i, ptable, p.size());
            bool isAnnagram = true;
            for (int j = 0; j < 26; j++)
            {
                if (table[j] != 0)
                {
                    isAnnagram = false;
                    char extra = 'a' + j;

                    if (table[j] < 0)
                    {
                        while (i + p.size() < s.size())
                        {
                            if (s[i + p.size()] == extra)
                            {
                                table[j]++;
                                if (table[j] == 0)
                                {
                                    break;
                                }
                            }
                            i++;
                        }
                    }
                    else
                    {
                        while (i <= s.size() - p.size())
                        {
                            if (s[i] == extra)
                            {
                                table[j]--;
                                if (table[j] == 0)
                                {
                                    break;
                                }
                            }
                            i++;
                        }
                    }

                    break;
                }
            }
            if (isAnnagram)
            {
                result.push_back(i);
                while (i <= s.size() - p.size() && s[i] == s[i + p.size()])
                {
                    i++;
                    result.push_back(i);
                }
            }
        }
        return result;
    }

    int *anagramDiff(string &s, int idx, int ptable[], int psize)
    {
        for (int i = 0; i < psize; i++)
        {
            ptable[s[i + idx] - 'a']++;
        }

        return ptable;
    }
};

// Others' top solution
class SolutionB
{
public:
    vector<int> findAnagrams(string s, string p)
    {
        if (s.size() < p.size())
        {
            return {};
        }

        int countP[26] = {0};
        int countS[26] = {0};

        vector<int> result;
        int diff = p.size();
        for (char &c : p)
        {
            ++countP[c - 'a'];
        }

        for (int i = 0; i < p.size(); ++i)
        {
            int idx = s[i] - 'a';
            ++countS[idx];
            if (countS[idx] <= countP[idx])
            {
                --diff;
            }
            else
            {
                ++diff;
            }
        }

        if (diff == 0)
        {
            result.push_back(0);
        }

        for (int i = p.size(); i < s.size(); ++i)
        {
            int idx = s[i] - 'a';
            ++countS[idx];

            if (countS[idx] <= countP[idx])
            {
                --diff;
            }
            else
            {
                ++diff;
            }

            // begin
            idx = s[i - p.size()] - 'a';
            --countS[idx];

            if (countS[idx] >= countP[idx])
            {
                --diff;
            }
            else
            {
                ++diff;
            }
            if (diff == 0)
            {
                result.push_back(i - p.size() + 1);
            }
        }
        return result;
    }
};

int main()
{
    string s1 = "cbaebabacd";
    string p1 = "abc";
    string s2 = "abab";
    string p2 = "ab";
    string s3 = "aaaaaaaaaa";
    string p3 = "aaaaaaaaaaaaa";
    string s4 = "";
    string p4 = "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz";
    string s5 = "";
    string p5 = "";

    vector<string> sgroups = {s1, s2, s3, s4, s5};
    vector<string> pgroups = {p1, p2, p3, p4, p5};

    int group;
    cout << "Please input group: ";
    cin >> group;

    Solution s;
    vector<int> result = s.findAnagrams(sgroups[group - 1], pgroups[group - 1]);
    output(result);

    SolutionB sb;
    vector<int> resultB = sb.findAnagrams(sgroups[group - 1], pgroups[group - 1]);
    output(resultB);

    return 0;
}
